Alias.script:
--syntax: od
door = door or {}
local door = door.first if door ~= nil then send("open " .. door) else cecho("<red>First door not set!\n<yellow>'setdoor xxx' to set first door.\n") end

--syntax: cd
door = door or {}
local door = door.first if door ~= nil then send("cl " .. door) else cecho("<red>First door not set!\n<yellow>'setdoor xxx' to set first door.\n") end

--syntax: setdoor xxx
door = door or {}
door.first = matches[2] cecho("First door is set as: <gold>" .. matches[2] .. "\n")

--syntax: deldoor
door = door or {}
door.first = nil cecho("<red>First door deleted!\n")

--syntax: pd
door = door or {}
local door = door.first if door ~= nil then send("pick " .. door) else cecho("<red>First door not set!\n<yellow>'setdoor xxx' to set first door.\n") end

--syntax: kd
door = door or {}
local door = door.first if door ~= nil then send("knock " .. door) else cecho("<red>First door not set!\n<yellow>'setdoor xxx' to set first door.\n") end

--syntax: ud
door = door or {}
local door = door.first if door ~= nil then send("unlock " .. door) else cecho("<red>First door not set!\n<yellow>'setdoor xxx' to set first door.\n") end

--syntax: ld
door = door or {}
local door = door.first if door ~= nil then send("lock " .. door) else cecho("<red>First door not set!\n<yellow>'setdoor xxx' to set first door.\n") end
