Alias.script:
--syntax: t1 xxx, t2 xxx, t3 xxx, t4 xxx, etc.
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
local index = tonumber(matches[2]) local tgt = matches[3] local color = colors[tgt] or "reset"
targeting[index] = tgt cecho(f"Target {index} set to: <{color}>{tgt}\n")

--syntax: tgts
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {} weaving = weaving or {}
if targeting.currtgt then local color = colors[targeting.currtgt] or "reset" cecho(f"Your current target is: <{color}>{targeting.currtgt}\n") end
local pkmode = (targeting.bashing and "bashing") or (targeting.charging and "charging") or (targeting.stabbing and "stabbing") or (targeting.weaving and "weaving") or "none" cecho(f"Your current pk mode is: {pkmode}\n") if targeting.weaving then cecho("Longweave set to: "..(weaving.longweave or "none").."\n") cecho("Shortweave set to: "..(weaving.shortweave or "none").."\n") end
for i, tgt in ipairs(targeting) do local color = colors[tgt] or "reset" cecho(f"Target {i} = <{color}>{tgt}\n") end

--syntax: tgt xxx
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
local tgt = matches[2] local color = colors[tgt] or "reset"
targeting.currtgt = tgt cecho(f"Current target set to: <{color}>{tgt}\n")
if color ~= "reset" then status_bar["target"]:echo(tgt:upper(), color, "c") else status_bar["target"]:echo(tgt:upper(), "white", "c") end

--syntax: tgt
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
if targeting.currtgt then local tgt = targeting.currtgt local color = colors[tgt] or "reset"
cecho(f"Current target set to: <{color}>{tgt}\n") else cecho("<red>Current target not set!\n") end
