if not wotmudmapper.dark then wotmudmapper.dark = true end wotmudmapper.roomname = "" wotmudmapper.roomdesc = "" wotmudmapper.roomexits = {}
local mapCommand, lastID --Specifying the type of movement. If not fleeing or following, then take from queue if not wotmudmapper.flee and not wotmudmapper.following then mapCommand = wotmudmapper.queue:popleft() else --else, fleeing or following if wotmudmapper.following then --treat following as a flee movement mapCommand = "f" --setting following at false for the following map movement wotmudmapper.following = false else --checking if next command in line is flee mapCommand = wotmudmapper.queue:checkleftvalue() --if yes, then take command from queue if isFlee(mapCommand) then wotmudmapper.queue:popleft() else --if no, then it is autowimpy flee mapCommand = "f" end --setting flee at false for the following map movement wotmudmapper.flee = false end end --finished specifying if movement was regular or a flee --loading the previous roomID. lastID = wotmudmapper.currentID local newID = {} --if mapCommand is a direction and you were in a valid room if isMovement(mapCommand) and (not table.is_empty(lastID)) then for k, v in pairs(lastID) do local rooms = wotmudmapper:isNearRoom(mapCommand, wotmudmapper.roomname, wotmudmapper.roomdesc, getRoomExits(v)) if not table.contains(newID, rooms[1]) then table.insert(newID, rooms[1]) end end wotmudmapper.currentID = newID --handling flees elseif isFlee(mapCommand) and (not table.is_empty(lastID)) then for k, v in pairs(lastID) do local rooms = getRoomExits(v) for kk, vv in pairs(rooms) do if not table.contains(newID, vv) then table.insert(newID, vv) end end end wotmudmapper.currentID = newID --if not looking at room, search for room from all rooms in the map elseif not (isLook(mapCommand) and table.size(lastID) == 1) then wotmudmapper.currentID={}
for k, v in pairs(getRooms()) do table.insert(wotmudmapper.currentID, k) end end
if table.is_empty(wotmudmapper.currentID) then wotmudmapper:echo("\nNo matches found.",false,true) elseif table.size(wotmudmapper.currentID) > 1 then wotmudmapper:echo("\nMultiple matches found.",false,true) else centerview(wotmudmapper.currentID[1])
--note room exits that lead to unknown rooms (as they may be DTs) local safe_rooms = {} for k,v in pairs(getRoomExits(wotmudmapper.currentID[1])) do table.insert(safe_rooms, string.sub(k,1,1)) end local unsafe_rooms = table.n_complement(wotmudmapper.roomexits, safe_rooms) if getExitStubs(wotmudmapper.currentID[1]) then for k, v in pairs(getExitStubs(wotmudmapper.currentID[1])) do if not table.contains(unsafe_rooms,string.sub(wotmudmapper.exitmap[v],1,1)) then table.insert(unsafe_rooms,string.sub(wotmudmapper.exitmap[v],1,1)) end end end for k,v in pairs(unsafe_rooms) do wotmudmapper:echo( "\nCaution. Direction " .. v .. " leads to an unknown room.", false, true ) end
--displaying the zone wotmudmapper.zone = getRoomUserData(wotmudmapper.currentID[1], "zone") if wotmudmapper.configs.showzone then cechoLink("\n<"..wotmudmapper.configs.zonecolor..">Zone: " .. wotmudmapper.zone, function() if wotmudmapper.zone ~= "" then if wotmudmapper.zone_urls[wotmudmapper.zone] ~= "" then openUrl("https://wotmud.fandom.com/wiki/"..wotmudmapper.zone_urls[wotmudmapper.zone]) end end end, "Click to open the WoTMUD wiki page for this zone", true) end
--showing the doors that have been stored for k, v in pairs({"north", "east", "south", "west", "up", "down"}) do local dir = string.sub(v, 1, 1) if getRoomUserData(wotmudmapper.currentID[1], dir) ~= "" then cecho("\n<"..wotmudmapper.configs.doorcolor..">Door " .. v .. ": <white>" .. getRoomUserData(wotmudmapper.currentID[1], dir)) end end end

