ScriptGroup.script:
communications = communications or {
    tabs = {"All", "Narrates", "Chats", "Tells", "Says", "Yells", "Misc"},
    tab_color = "navy"
  }

communications.defaults = {
  dbug = true,
  comms_window={
      visible=true,
      x="-37%",
      y="73%",
      w="37%",
      h="27%",
      fontsize=12,
      dock=false},
  colors = {narrates="royal_blue",
            chats="dark_green",
            tells="firebrick",
            yells="gold",
            immortals="dodger_blue"}
}

Script.script:
function CommsOnSysDisconnectionEvent()
  communications.configs = communications.configs or {}

  communications:echo("Saving communications configurations.\n")
  table.save(getMudletHomeDir() .. "/communications.configs.lua", communications.configs)
  communications:echo("Communications configurations saved.\n")
end

function communications:append(tab) selectCaptureGroup(1) copy() local time = getTime() local time_string = string.format("%02d:%02d ",time.hour,time.min)
communications.All:echo(time_string) communications[tab]:echo(time_string)
communications.All:appendBuffer() communications[tab]:appendBuffer() end

function communications:show_help(cmd) if cmd then cmd = cmd:gsub(" ","_") if not self.help[cmd] then self:echo("No help file on that command.\n",false,true) return end else cmd = 1 end for w in self.help[cmd]:gmatch("(.-)\n") do local before, target, text, after = w:match("(.*)<link: (.-)>(.*)<\/link>(.*)") if target then cecho(before) fg("yellow") setUnderline(true) if target == "1" then echoLink(text, [[communications:show_help()]], "View: comms help", true) else echoLink(text, [[communications:show_help("]] .. target .. [[")]], "View: comms help " .. target, true) end setUnderline(false) resetFormat() if after then cecho(after) end else cecho(w) end cecho("\n") end end
--definition of communications.help and sub-parts communications.help = { [[ <cyan>Communications Script<reset>
This script will capture narrates, chats, tells, says, yells, immortal speaking, and rat/raven reports, and store them in a tabbed chat window.
You can copy/paste output from the window by selecting the text, right clicking, and copying (ctrl + C will NOT work).
The roomnames in rat/raven reports are clickable and will search the map for possible matches.
Below is a brief overview of commonly used commands that this script includes. Additional information for further areas is available as well, though every command may not be described here. All commands can be found in the appropriate communications folder under mudlet aliases.
<cyan>Common Commands:<reset> <link: 1>comms help (optional subfolder name)</link> - Shows either this help file or the help file for the subfolder given (e.g. comms help comms colors, comms help comms window, etc.) <yellow>comms dock<reset> - Make the communications window either dock to the left or right half of the screen, or float <yellow>comms show<reset> - Displays or hides a communications window in a fixed position <yellow>comms debug<reset> - Toggles debug mode, in which extra messages are shown with the intent of assisting in troubleshooting. Default: on <yellow>comms configs reset<reset> - Reset the communications configuration values.
<cyan>Information subfolders:<reset> These are sub-folders containing information for more commands <link: comms_colors>comms colors</link> - Commands used in changing communications colors <link: comms_window>comms window</link> - Altering the size and position of the communications display
]] }
communications.help.comms_colors = [[ <cyan>Communications Colors:<reset> These commands are used to change the colors of the communications lines <yellow>comms color type color<reset> - Chance the color of the given communications type to the provided color Example: comms color narrates green, comms color says white
]]
communications.help.comms_window = [[ <cyan>Communications Window<reset> <yellow>comms window setting value<reset> - This command changes the given setting to the given value, detailing how the communications window should appear. Possible settings are: <yellow>x<reset> - Specify the top left corner of the communications window as a percentage of the screen width. Default: -37 <yellow>y<reset> - Specify the top left corner of the communications window as a percentage of the screen height. Default: 0 <yellow>w<reset> - Specify the width of the communications window as a positive percentage of the screen width. Default: 37 <yellow>h<reset> - Specify the height of the communications window as a positive percentage of the screen height. Default: 73 <yellow>fontsize<reset> - Specify the font size of the communications window. Default: 12 <yellow>comms dock<reset> - Make the communications window either dock to the left or right half of the screen, or float <yellow>comms show<reset> - Displays or hides a communications window in a fixed position
]]

local w, h = getMainWindowSize()
setBorderBottom(5 * h / 100)

statusbar=statusbar or {}

statusbar.main=Geyser.Container:new({name = "statusbar.main", x = 0, y = "-3%", width = "60%", height = "3%"})

statusbar.mood = Geyser.Label:new({name = "statusbar.mood", x = "0%", y = "0%", width = "5%", height = "100%"}, statusbar.main)
statusbar.mood:echo("Mood", "white", "c")
statusbar.mood:setStyleSheet([[
  background-color: black;
  border: 1px solid gray;
]])

