ScriptGroup.script:
--define mudlet required mapper variables and wotmudmapper table mudlet = mudlet or {} mudlet.mapper_script = true wotmudmapper = wotmudmapper or {} wotmudmapper.configs = wotmudmapper.configs or {}
--mapper variables
--table to hold list of possible current room IDs wotmudmapper.currentID = wotmudmapper.currentID or {} --current room name captured by triggers wotmudmapper.roomname = wotmudmapper.roomname or "" --current room description captured by triggers wotmudmapper.roomdesc = wotmudmapper.roomdesc or "" --table to hold list of room exits captured by triggers. list may include exits not in the map data. map data may include exits not in the list. wotmudmapper.roomexits = wotmudmapper.roomexits or {} --current zone name pulled from map data wotmudmapper.zone = wotmudmapper.zone or "" --true only if wotmudmapper.roomdesc is empty string or in the dark wotmudmapper.brief = wotmudmapper.brief or false --true only if night/blind/fog triggers match wotmudmapper.dark = wotmudmapper.dark or false --used by triggers to determine whether most recent movement was a manual flee or autoflee wotmudmapper.flee = wotmudmapper.flee or false --set to true on "You follow (.+)\.$" wotmudmapper.following = wotmudmapper.following or false --variable to hold door direction. only used in mapping mode to create doors wotmudmapper.door = wotmudmapper.door or "n" --true only if wotmudmapper.roomname matches a room name from inside The Ways zone wotmudmapper.inways = wotmudmapper.inways or false
--default configuration values to be used or updated in wotmudmapper.configs wotmudmapper.defaults = { on = true, --true if the map should actively follow you mapping = false, --true if mapping mode is turned on dbug = true, --debug for echoing messages mapbrief = false, --true if map triggers should manually delete room description lines from the screen bothways = true, --true if new room and old room should be connected both ways in mapping mode autojoin = false, --true if rooms should be auto-connected based on exits and proximity in mapping mode forceroom = false, --true if a new room should be force created in mapping mode envcolor = 20, --default environment number for mapping offline = false, --true if map is being used in offline mode roomID = 1, --last known room ID before profile is shut down showzone = true, --true if zone information should be displayed below room exits zonecolor = "turquoise", --color of zone information displayed below room exits doorcolor = "blue", --color of door information displayed below room exits speedwalk = true, --whether or not double clicking rooms will make you speedwalk map_window = { widget = false, --true if the map widget should be used instead of an Adjustable.Container visible = true, --true if map should be shown x = "-37%", --left border of map Adjustable.Container y = "0%", --top border of map Adjustable.Container w = "37%", --width of map Adjustable.container h = "73%", --height of map Adjustable.container zoom = 50, --map window zoom level dock = false --true if map Adjustable.Container can be docked to left or right of screen } }
--default map room type environment numbers and colors wotmudmapper.room_type_env_nums = { inside = 20, wilderness = 21, road = 22, horse = 23, swamp = 24, water = 25, drink = 26, weaponsmith = 27, armorer = 28, blacksmith = 29, bank = 30, stables = 31, rent = 32, grocer = 33, warrior = 34, rogue = 35, hunter = 36, pk = 37, city = 38, herb = 39, tailor = 40, nochannel = 41, portalstone = 42, trees = 43, smob = 44, herbalist = 45 } wotmudmapper.default_map_colors = { inside = "white", wilderness = "forest_green", road = "brown", horse = "burlywood", swamp = "dark_violet", water = "blue", drink = "medium_aquamarine", weaponsmith = "steel_blue", armorer = "gray", blacksmith = "dark_orange", bank = "gold", stables = "purple", rent = "green", grocer = "red", warrior = "sienna", rogue = "dark_slate_gray", hunter = "yellow_green", pk = "light_gray", city = "brown", herb = "yellow", tailor = "magenta", nochannel = "gray", portalstone = "gray", trees = "lawn_green", smob = "firebrick", herbalist = "yellow" }
--table of exit to number associations if not wotmudmapper.exitmap then wotmudmapper.exitmap = { n = 1, north = 1, ne = 2, northeast = 2, nw = 3, northwest = 3, e = 4, east = 4, w = 5, west = 5, s = 6, south = 6, se = 7, southeast = 7, sw = 8, southwest = 8, u = 9, up = 9, d = 10, down = 10, ["in"] = 11, ["out"] = 12, [1] = "north", [2] = "northeast", [3] = "northwest", [4] = "east", [5] = "west", [6] = "south", [7] = "southeast", [8] = "southwest", [9] = "up", [10] = "down", [11] = "in", [12] = "out", } end
--List functions List = {}
function List:new() local list = {first = 0, last = -1} setmetatable(list, self) self.__index = self return list end
function List:pushright(value) local last = self.last + 1 self.last = last self[last] = value end
function List:popleft() local first = self.first local value = self[first] --to allow garbage collection self[first] = nil self.first = first + 1 if first >= self.last then self.first=0 self.last=-1 end if value then wotmudmapper:echo("Removed command ("..value..") from queue.\n",true) else wotmudmapper:echo("No commands left to remove from queue.\n",true) end return value end
function List:checkleftvalue() return self[self.first] end
--queue to store movement commands wotmudmapper.queue = List:new()

Script.script:
function wotmudmapper:set_configs() --local copies of map configs/colors and their defaults local defaults = table.deepcopy(self.defaults) or {} local configs = table.deepcopy(self.configs) or {} local default_colors = table.deepcopy(self.default_map_colors) or {} local colors = table.deepcopy(self.map_colors) or {}
--if config and color files exist on disk, load them in self:echo("Loading map configurations.\n", true) -- load stored configs from file if it exists if io.exists(getMudletHomeDir().."/wotmudmapper.configs.lua") then table.load(getMudletHomeDir().."/wotmudmapper.configs.lua",configs) end if io.exists(getMudletHomeDir().."/wotmudmapper.map_colors.lua") then table.load(getMudletHomeDir().."/wotmudmapper.map_colors.lua",colors) end self:echo("Map configurations loaded.\n", true)
-- overwrite default values with stored config values and save to disk configs = table.update(defaults, configs) self.configs = table.deepcopy(configs) table.save(getMudletHomeDir() .. "/wotmudmapper.configs.lua", self.configs) colors = table.update(default_colors, colors) self.map_colors = table.deepcopy(colors) table.save(getMudletHomeDir() .. "/wotmudmapper.map_colors.lua", self.map_colors) end

function wotmudmapper:show_help(cmd) if cmd then cmd = cmd:gsub(" ","_") if not self.help[cmd] then self:echo("No help file on that command.\n",false,true) return end else cmd = 1 end for w in self.help[cmd]:gmatch("(.-)\n") do local before, target, text, after = w:match("(.*)<link: (.*)>(.*)</link>(.*)") if target then cecho(before) fg("yellow") setUnderline(true) if target == "1" then echoLink(text, [[wotmudmapper:show_help()]], "View: map help", true) else echoLink(text, [[wotmudmapper:show_help("]] .. target .. [[")]], "View: map help " .. target, true) end setUnderline(false) resetFormat() if after then cecho(after) end else cecho(w) end cecho("\n") end end
--definition of wotmudmapper.help and sub-parts wotmudmapper.help = { [[ <cyan>WoTMUD Mapping Script<reset>
This script will both follow movements through currently known rooms and allow for the creation of new rooms. It requires "color complete" to be turned on in the MUD for room names to be properly captured, and "autoexits" to be turned on to be able to determine room exits. Both are necessary for the script to function properly. The script will work with or without "brief" mode turned on in the MUD, though it will be more accurate with brief mode off. You can choose to have the script hide room descriptions instead.
This script defines the necessary function so that you can speedwalk by double clicking on rooms.
Below is a brief overview of commonly used commands that this script includes. Additional information for further areas is available as well, though every command may not be described here. All commands can be found in the appropriate mapper folder under mudlet aliases.
<cyan>Common Commands:<reset> <link: 1>map help (optional subfolder name)</link> - Shows either this help file or the help file for the subfolder given (e.g. map help booleans, map help doors, etc.) <yellow>map show<reset> - Displays or hides a map window in a fixed position. Toggle off and restart mudlet in order to make the map a dockable window via the mudlet toolbar map button <yellow>map debug<reset> - Toggles debug mode, in which extra messages are shown with the intent of assisting in troubleshooting. Default: on <yellow>resetqueue<reset> - Clears the list of movement commands maintained by the script. Useful if map appears to not be following correctly, or if aliases to open doors appear to not work properly <yellow>map update<reset> - Downloads, saves, and loads the latest version of the WoTMUD_map.dat file from https://github.com/weisluke/WoTMUD <yellow>map brief<reset> - Toggle whether map script should hide or show the descriptions from rooms. Default: show
<cyan>Information subfolders:<reset> These are sub-folders containing information for more commands <link: booleans>booleans</link> - Map variables that can be toggled on/off <link: creation>creation</link> - Commands used in creating new areas and rooms <link: room_data>room data</link> - Commands used in viewing and changing room data <link: doors>doors</link> - Commands used in creating and editing doors <link: room_colors>room colors</link> - Commands used in viewing and changing room colors <link: key_variables>key variables</link> - Variables used by the script to keep track of important information <link: map_window>map window</link> - Altering the size and position of the map display <link: misc>misc</link> - Miscellaneous commands
]] }
wotmudmapper.help.booleans = [[ <cyan>Mapper Booleans<reset> These commands control configuration options that can be toggled on/off <yellow>mapping<reset> - Turn mapping mode on/off. Default: off (will reset to off when mudlet is closed) <yellow>map bothways<reset> - Toggle whether map should set one-way or two-way exits when creating new rooms. Default: on <yellow>map autojoin<reset> - Toggle whether map should auto-join exits to nearby rooms when mapping, or whether you must actually enter a room for a connection to be made. Default: off <yellow>map brief<reset> - Toggle whether map script should hide or show the descriptions from rooms. Default: show <yellow>forceroom<reset> - Force the map to create a new room when a direction is input (as opposed to potentially linking to a room that already exists). Useful when mapping in areas that have multiple rooms with the same name (e.g. Blight) <yellow>map debug<reset> - Toggles debug mode, in which extra messages are shown with the intent of assisting in troubleshooting. Default: on <yellow>map showzone<reset> - Toggle whether the map script should display or hide zone info <yellow>map offline<reset> - Toggle whether map should be turned into offline mode, enabling offline movement aliases and numpad key bindings that allow you to move around and look via single character direction/look inputs (i.e. n/e/s/w/u/d/l), or through numpad numbers. Default: off (will reset to off, and disable offline movement aliases/keys, when mudlet is closed)
]]
