function wotmudmapper:door_com_dir(com, dir) --start out with list of current possible rooms local rooms = table.deepcopy(self.currentID) --loop over all commands in the movement queue for i = self.queue.first, self.queue.last, 1 do --variable to hold new possible rooms entered by the current movement command local temp_rooms = {} --for all possible rooms currently stored for _, room in pairs(rooms) do --get the exits in the room local exits = getRoomExits(room) --if the current movement command is a flee if self.queue[i] == "f" then --add all possible exits from current room to list of new possible rooms for dir, id in pairs(exits) do table.insert(temp_rooms, id) end --also add current room, to handle failed flees --commenting this out for now as I determine whether or not it makes the most sense --table.insert(temp_rooms, room) --if the current movement command is a look elseif self.queue[i] == "l" then --insert the current room into the list of new possible rooms table.insert(temp_rooms, room) --otherwise, the command is a movement else --if current movement command would take you to new room, add that new room to list of new possible rooms if exits[self.exitmap[self.exitmap[self.queue[i]]]] then table.insert(temp_rooms, exits[self.exitmap[self.exitmap[self.queue[i]]]]) --otherwise, the movement command would fail, so add current room to list of new possible rooms else table.insert(temp_rooms, room) end end end --update rooms to loop over for next movement command. union ensures there are no duplicates rooms = table.n_union(temp_rooms, temp_rooms) end
--variable to hold final list of valid rooms local finalrooms = {}
--for all rooms that we found for _, id in pairs(rooms) do --if there is a door in the direction desired, add it to the list of valid rooms if getRoomUserData(id, dir) ~= "" then table.insert(finalrooms, id) end end --if list of valid rooms contains only one, send the command for that door if table.size(finalrooms) == 1 then local door = getRoomUserData(finalrooms[1], dir) send(com .. " " .. door .. " " .. dir) --otherwise send blank command else send(com .. " " .. dir) end end

--returns the ID of the room with matching name/desc/exits, or empty list
function wotmudmapper:find_room(name, desc, exits) local roommatches = {}
for id, rname in pairs(searchRoom(name, true, true)) do --if brief mode on, add all rooms with matching name to possible matches if self.brief then table.insert(roommatches, id) --if brief mode off, check description before adding elseif getRoomUserData(id, "description") == desc then table.insert(roommatches, id) end end --if more than one possible match, narrow based on exits if table.size(roommatches) > 1 then roommatches = self:find_matching_exits(roommatches, exits) end return roommatches end

--assumes exits is of the form {"n","e","s"}
function wotmudmapper:find_matching_exits(rooms, exits_seen)
local roommatches = {}
local map_exits, seen_minus_map, map_minus_seen, alldoors
for _, room in pairs(rooms) do map_exits = {} --create table of exit keys (grabbing first letter of "north","south",etc.) for dir, id in pairs(getRoomExits(room)) do table.insert(map_exits, dir:sub(1, 1)) end --if there are exit stubs leading to unmapped rooms, include those if getExitStubs(room) then for _, dirnum in pairs(getExitStubs(room)) do table.insert(map_exits, self.exitmap[dirnum]:sub(1, 1)) end end
--comparison tables of exits seen minus map exits from room seen_minus_map = table.n_complement(exits_seen, map_exits) --comparison tables of map exits from room minus exits seen map_minus_seen = table.n_complement(map_exits, exits_seen)
--if room exits match seen exits, insert room into possible matches if table.is_empty(seen_minus_map) and table.is_empty(map_minus_seen) then table.insert(roommatches, room) --if room has extra exits from seen exits --and ALL extra exits are doors (which could be hidden and closed) --then add room to possible matches elseif table.is_empty(seen_minus_map) and not table.is_empty(map_minus_seen) then alldoors = true for _, dir in pairs(map_minus_seen) do if getRoomUserData(room, dir) == "" then alldoors = false break end end if alldoors then table.insert(roommatches, room) end end end return roommatches end

function wotmudmapper:mapper_installer_success(event, name) --if installed file is not the desired one if name ~= "mapper_scripts" then return end self:show_help() self:set_configs() self:set_map_colors()
self:show_map(self.configs.map_window.visible) end
registerAnonymousEventHandler("sysInstallPackage", "wotmudmapper:mapper_installer_success")

function wotmudmapper:set_player_location(event, room)
  self.currentID={room}
end

registerAnonymousEventHandler("sysManualLocationSetEvent", "wotmudmapper:set_player_location")
