    border-style: solid;
    border-color: green;
    border-radius: 3px;
  ]]

practice_trainer.style_sheets.minus = [[
    background-color: white;
    border-width: 3px;
    border-style: solid;
    border-color: red;
    border-radius: 3px;
  ]]

practice_trainer.style_sheets.scrollable = [[
    background-color: white;
    border-width: 3px;
    border-style: solid;
    border-color: gray;
    border-radius: 3px;
  ]]

practice_trainer.style_sheets.clickable = [[
    background-color: gray;
