    border-color: gray;
    border-radius: 3px;
  ]]

function practice_trainer:container_params(name, x, y, w, h)

  return {
    name = name,
    x = x.."%",
    y = y.."%",
    width = w.."%",
    height = h.."%",
  }
end

function practice_trainer:command_line_params(name, x, y, w, h, msg)
  return {
    name = name,
    x = x.."%",
    y = y.."%",
    width = w.."%",
    height = h.."%",
    stylesheet = "border: 1px solid silver;",
  }
end

practice_trainer.pracs.queue = practice_trainer.pracs.queue or {}
function practice_trainer:push_queue(skill) table.insert(self.pracs.queue, skill) end
function practice_trainer:pop_queue(skill) local skill_pos = table.collect( self.pracs.queue, function(key, value) if value == skill then return true end end ) local max_pos = table.maxn(skill_pos) table.remove(self.pracs.queue, max_pos) end
