Script.script:
status_bar = status_bar or {}

status_bar.configs = status_bar.configs or 
  {
    fontsize = 10,
    visible = true,
    x = "0%",
    y = "-3%",
    w = "60%",
    h = "3%"
  }

status_bar.char = status_bar.char or
  {
    mood = nil,
    posture = nil,
    hps = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    sps = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    dps = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    mvs = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    autoflee = nil,
    fleelag = {
      min = nil,
      max = nil
    },
    charge_pulses = {
      min = nil,
      max = nil
    },
    notice = nil,
    sneak = nil,
    one_power = nil,
    armor = nil,
    int = nil,
    wil = nil,
    con = nil,
  }

function status_bar:sys_disconnection(event)
  status_bar.char = {
    mood = nil,
    posture = nil,
    hps = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    sps = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    dps = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    mvs = {
      max = nil,
      current = nil,
      est_max = nil,
      est_min = nil
    },
    autoflee = nil,
    fleelag = {
      min = nil,
      max = nil
    },
    charge_pulses = {
      min = nil,
      max = nil
    },
    notice = nil,
    sneak = nil,
    one_power = nil,
    armor = nil,
    int = nil,
    wil = nil,
    con = nil,
  }
  --save configuration to disk
  table.save(getMudletHomeDir() .. "/status_bar.configs.lua", self.configs)
end

registerAnonymousEventHandler("sysExitEvent", "status_bar:sys_disconnection")
registerAnonymousEventHandler("sysDisconnectionEvent", "status_bar:sys_disconnection")

function status_bar:is_weaving(event, com) com = com:lower()
if table.contains({"chann", "channe", "channel"}, com:split()[1]) then local weave = com:match("'(.+)'")
if weave then self:update_sps_with_weave(weave) end end end
registerAnonymousEventHandler("sysDataSendRequest", "status_bar:is_weaving")
