weave_trainer.pracs.queue = weave_trainer.pracs.queue or {}
function weave_trainer:push_queue(skill) table.insert(self.pracs.queue, skill) end
function weave_trainer:pop_queue(skill) local skill_pos = table.collect( self.pracs.queue, function(key, value) if value == skill then return true end end ) local max_pos = table.maxn(skill_pos) table.remove(self.pracs.queue, max_pos) end
