Trigger.script:
if not wotmudmapper.dark then wotmudmapper.dark = true end wotmudmapper.roomname = "" wotmudmapper.roomdesc = "" wotmudmapper.roomexits = {}
local com, lastID --Specifying the type of movement. If not fleeing or following, then take from queue if not wotmudmapper.flee and not wotmudmapper.following then com = wotmudmapper.queue:popleft() else --else, fleeing or following if wotmudmapper.following then --treat following as a flee movement com = "f" --setting following at false for the following map movement wotmudmapper.following = false else --checking if next command in line is flee com = wotmudmapper.queue:checkleftvalue() --if yes, then take command from queue if wotmudmapper:is_flee(com) then wotmudmapper.queue:popleft() else --if no, then it is autowimpy flee com = "f" end --setting flee at false for the following map movement wotmudmapper.flee = false end end --finished specifying if movement was regular or a flee --loading the previous roomID. lastID = wotmudmapper.currentID local newID = {} --if com is a direction and you were in a valid room if wotmudmapper:is_movement(com) and (not table.is_empty(lastID)) then for _, id in pairs(lastID) do if wotmudmapper:is_waygate(id) and com == "d" then for _, room in pairs(wotmudmapper:get_waygates(id)) do table.insert(newID, room) end end table.insert(newID, wotmudmapper:is_near_room(com, getRoomExits(id))) end wotmudmapper.currentID = table.n_union(newID, newID) --handling flees elseif wotmudmapper:is_flee(com) and (not table.is_empty(lastID)) then for _, id in pairs(lastID) do if wotmudmapper:is_waygate(id) then for _, room in pairs(wotmudmapper:get_waygates(id)) do table.insert(newID, room) end end for dir, to_id in pairs(getRoomExits(id)) do table.insert(newID, to_id) end end wotmudmapper.currentID = table.n_union(newID, newID) --if not looking at room, search for room from all rooms in the map elseif not (wotmudmapper:is_look(com) and table.size(lastID) == 1) then wotmudmapper.currentID={} for id, rname in pairs(getRooms()) do table.insert(wotmudmapper.currentID, id) end end
if table.is_empty(wotmudmapper.currentID) then wotmudmapper:echo("\nNo matches found.",false,true) elseif table.size(wotmudmapper.currentID) > 1 then wotmudmapper:echo("\nMultiple matches found.",false,true) else centerview(wotmudmapper.currentID[1]) wotmudmapper:show_zone_door_info() end
